# Solution Part2  
processed = 0
chars = []
for char in input:
    processed += 1
    chars.append(char)
    if len(chars) > 14:
        chars.pop(0)
        if ((chars.count(chars[0]) == 1) and
            (chars.count(chars[1]) == 1) and
            (chars.count(chars[2]) == 1) and
            (chars.count(chars[3]) == 1) and
            (chars.count(chars[4]) == 1) and
            (chars.count(chars[5]) == 1) and
            (chars.count(chars[6]) == 1) and
            (chars.count(chars[7]) == 1) and
            (chars.count(chars[8]) == 1) and
            (chars.count(chars[9]) == 1) and
            (chars.count(chars[10]) == 1) and
            (chars.count(chars[11]) == 1) and
            (chars.count(chars[12]) == 1) and
            (chars.count(chars[13]))):
            print("Signal Received!")
            print("Characters processed: ", processed)
            print(chars)
            break